class Solution:
    def nextElement(self, head: ListNode, val: int) -> ListNode:
        while head != None and head.val == val:
            head = head.next
        return head

    def removeElements(self, head: ListNode, val: int) -> ListNode:
        head = self.nextElement(head, val)
        cur = head
        while cur != None:
            cur.next = self.nextElement(cur.next, val)
            cur = cur.next
        return head
