impl Solution {
    pub fn remove_element(nums: &mut Vec<i32>, val: i32) -> i32 {
        let mut ind = 0;
        while ind < nums.len() {
            if nums[ind] == val {
                nums.swap_remove(ind);
            } else {
                ind += 1;
            }
        }
        nums.len() as i32
    }
}
