class Solution
{
public:
    bool isValidSudoku(vector<vector<char>> &board)
    {
        set<int> s;
        for (int i = 0; i < 9; i++)
        {
            s.clear();
            for (int j = 0; j < 9; j++)
            {
                if (board[i][j] == '.')
                    continue;
                if (s.count(board[i][j]))
                    return false;
                s.insert(board[i][j]);
            }
        }
        for (int i = 0; i < 9; i++)
        {
            s.clear();
            for (int j = 0; j < 9; j++)
            {
                if (board[j][i] == '.')
                    continue;
                if (s.count(board[j][i]))
                    return false;
                s.insert(board[j][i]);
            }
        }
        for (int i = 0; i < 9; i += 3)
        {
            for (int j = 0; j < 9; j += 3)
            {
                s.clear();
                for (int k = 0; k < 3; k++)
                    for (int l = 0; l < 3; l++)
                    {
                        if (board[i + k][j + l] == '.')
                            continue;
                        if (s.count(board[i + k][j + l]))
                            return false;
                        s.insert(board[i + k][j + l]);
                    }
            }
        }
        return true;
    }
};
